<script>
export default {
  name: "navigationMenu",
  data() {
    return {
      localIsOpen: this.isOpen
    };
  },
  props: ["isOpen"],
  methods: {
    clickedOutside(event) {
      if (event.target !== this.$refs.navigationMenu) {
        if (!this.isInWhiteList(event.target)) this.localIsOpen = false;
      }
      console.log(event.target);
    },
    isInWhiteList(el) {
      if (el === this.$refs.navigationMenu) return true;
      if (el === this.$refs.burger) return true;
      // All chidren from dropdown
      if (this.$refs.navigationMenu !== undefined) {
        const children = this.$refs.navigationMenu.querySelectorAll("*");
        for (const child of children) {
          if (el === child) {
            return true;
          }
        }
      }
      // All children from trigger
      if (this.$refs.burger !== undefined) {
        const children = this.$refs.burger.querySelectorAll("*");
        for (const child of children) {
          if (el === child) {
            return true;
          }
        }
      }

      return false;
    }
  },
  mounted() {
    console.log("mounted");
  },
  created() {
    if (typeof window !== "undefined") {
      document.documentElement.addEventListener("click", this.clickedOutside, {passive: true});
    }
  },
  beforeDestroy() {
    if (typeof window !== "undefined") {
      document.documentElement.removeEventListener("click", this.clickedOutside);
    }
  }
};
</script>

